import React, { Component } from 'react';
import CourseDataService from './CourseDataService';
import ButtonDataService from './ButtonDataService';

class ListCoursesComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            courses: [],
            message: 'Testeroni'
        }
        this.refreshCourses = this.refreshCourses.bind(this)
    }

    componentDidMount() {
        this.refreshCourses();
    }

    refreshCourses() {
        CourseDataService.retrieveAllCourses("in28minutes")//HARDCODED
            .then(
                response => {
                    console.log(response);
                    this.setState({ courses: response.data })
                }
            )
    }
    
    getButton() {
        ButtonDataService.retrieveButtonName()
            .then(
                response => {
                    console.log(response);
                    this.setState( {message: response.data})
                }
            )

    }
    
    render() {
        return (
            <div className="container">
                <h3>All Courses</h3>
                <div className="container">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Id</th>
                                <th>Description</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.courses.map(
                                    course =>
                                        <tr key={course.id}>
                                            <td>{course.id}</td>
                                            <td>{course.description}</td>
                                        </tr>
                                )
                            }
                        </tbody>
                    </table>
                    <br>
                    </br>
                    <button type="button" onClick={this.getButton().bind}>{this.state.message}</button>
                </div>
            </div>
        )
    }
}

export default ListCoursesComponent
